mapper.update:
update user_info
set user_number_ = #{userNumber,jdbcType=VARCHAR},
  user_nickname_ = #{userNickname,jdbcType=VARCHAR},
  user_email_ = #{userEmail,jdbcType=VARCHAR},
  user_phone_ = #{userPhone,jdbcType=VARCHAR}
where id_ = #{id,jdbcType=INTEGER}

mapper.select:
select id_, user_number_, user_nickname_, user_email_, user_phone_
from user_info
where id_ = #{id,jdbcType=INTEGER}
